var path = path || require('path');
var walkSync = function(dir, filelist) {
    var path = path || require('path');
    var fs = fs || require('fs'),
        files = fs.readdirSync(dir);
    filelist = filelist || [];
    files.forEach(function(file) {
      if (fs.statSync(path.join(dir, file)).isDirectory() && file != "node_modules") {
        filelist.push(__dirname.replace("rec\\node_modules\\recursive","") + path.join(dir, file).replace("..\\",""));
        filelist = walkSync(path.join(dir, file), filelist);
      }
      else {
        filelist.push(__dirname.replace("rec\\node_modules\\recursive","") + path.join(dir, file).replace("..\\",""));
      }
    });
    return filelist;
  };

  var obj = {};
  var arra = [];
  walkSync("../",arra);
  var str = "";

  arra.forEach(element => {
    if(element.endsWith(".js")){
      //obj[element.replace(".js","")] = element;
      obj[path.basename(element,".js")] = element;
    }
  }); 
  module.exports = obj;